import React, { useState } from "react";
// import { useEffect, useMemo } from "react";
import styles from "./app.module.css";
// import { URL } from "../../utils/constants";
// import { data } from "../../utils/data";
import BurgerIngredients from "../burger-ingredients/burger-ingredients";
import BurgerConstructor from "../burger-constructor/burger-constructor";
import AppHeader from "../app-header/app-header";
// // import { ConstructorElement } from '@ya.praktikum/react-developer-burger-ui-components'

import IngredientDetails from '../modal/ingredient-details/ingredient-details';
import OrderDetails from '../modal/order-details/order-details';
import { getIngredients } from "../../services/actions/ingredients-action";
import { useDispatch, useSelector } from "react-redux";
import {
  ingredientsIsLoadingSelector,
  burgerIngredientsSelector,
  bunSelector,
  ingredientsDataSelector,
  ingredientsErrorSelector,
} from "../../services/selectors/ingredients-selector";
// import {
//   modalOpenSelector, isVisibleModalSelector, closeModalSelector

// } from "../../services/selectors/modal-selector";
import {
  orderErrorSelector,
  orderLoadingSelector,
  orderNameSelector,
  orderNumberSelector,
} from "../../services/selectors/order-selector";
import { closeModalAction } from "../../services/actions/modal-action"
import { orderSuccess } from "../../services/actions/order-actions";
import { openModalAction } from "../../services/actions/modal-action";
import { CLEAR_CONSTRUCTOR } from "../../services/actions/burger-constructor-action";
import { makeOrder } from "../../utils/api";


function App() {
  const dispatch = useDispatch();
  const orderLoading = useSelector(orderLoadingSelector);
  const orderName = useSelector(orderNameSelector);
  const orderNumber = useSelector(orderNumberSelector);
  const orderError = useSelector(orderErrorSelector);
  const ingredients = useSelector(burgerIngredientsSelector);
  const bun = useSelector(bunSelector);
  // const [data, setData] = React.useState([]);
  // const [hasError, setHasError] = React.useState(false);
  // const [isLoading, setIsLoading] = React.useState(false);


  const [modalData, setModalData] = React.useState(null);
  // const [modal, setModal] = React.useState(null);
  // const [isVisibleModal, setIsVisibleModal] = React.useState(false);

  // const modal = useSelector(
  //   modalOpenSelector
  // );
  // const isVisibleModal = useSelector(
  //   isVisibleModalSelector
  // );
  // const closeModal = useSelector(
  //   closeModalSelector
  // );
  // const getResponse = res =>
  //   res.ok
  //     ? res.json()
  //     : Promise.reject(`error: ${res.status} ${res.statusText}`)

  // function getData() {
  //   setIsLoading(true);
  //   fetch(
  //     URL, { method: "GET" }
  //   ).then(getResponse)
  //     .then(
  //       object => (object.success && object.data.length)
  //         ? object.data
  //         : Promise.reject(`error: ${object}`)
  //     ).then(
  //       object => {
  //         setData(object);
  //       }
  //     ).catch(
  //       error => {
  //         console.error("error:", error.message);
  //         setHasError(true);
  //       }
  //     ).finally(
  //       () => {
  //         setIsLoading(false);
  //       }
  //     );
  // };

  // React.useEffect(
  //   getData,
  //   []
  // );

  React.useEffect(() => {
    dispatch(getIngredients());
  }, [dispatch]);
  React.useEffect(() => {
    if (orderName && orderNumber) {
      dispatch({
        type: CLEAR_CONSTRUCTOR,
      });
      openModalAction("order");
    }
  }, [orderName, orderNumber, openModalAction]);


  // const openModal = React.useCallback(
  //   () => {
  //     dispatch(openModalAction());
  //   },
  //   []
  // );

  // const closeModal = React.useCallback(
  //   () => {
  //     dispatch(closeModalAction());
  //   },
  //   []
  // );
  const cardOnClick = React.useCallback(
    (item) => {
      return () => {
        setModalData(item);
        dispatch(openModalAction("ingredient"));
        // setModal("ingredient");
        dispatch(orderSuccess("ingredient"));

        // openModal();
      };
    },
    []
  );

  // const orderOnClick = React.useCallback(
  //   () => {
  //     // openModal();
  //     dispatch(openModalAction("order"));
  //     // dispatch(
  //     //   order(
  //     //     ingredients.map((ingredient) => ingredient._id).concat(bun._id)
  //     //   )
  //     // );

  //   },
  //   []
  // );


  const isLoading = useSelector(ingredientsIsLoadingSelector);
  // const ingredients = useSelector(ingredientsDataSelector);
  const error = useSelector(ingredientsErrorSelector);
  if (isLoading) {
    return <p className="text text_type_main-large">Загрузка...</p>;
  }

  if (error) {
    return <p className="text text_type_main-large">{error}</p>;
  }





  return (
    <div className={styles.app}>
      <pre className={styles.container}>
        <AppHeader />
        <div className={styles.content}>
          {/* < BurgerIngredients data={ingredients} cardOnClick={cardOnClick} count={1} />
          <BurgerConstructor data={ingredients} orderOnClick={orderOnClick} /> */}
          < BurgerIngredients cardOnClick={cardOnClick} count={1} />
          <BurgerConstructor />
        </div>
      </pre>
      {/* {
        isVisibleModal &&
        <Modal
          title={modal === "ingredient" ? "Детали ингредиента" : ""}
          isVisible={isVisibleModal}
          onClose={closeModal}
        > */}
      {/* {modal === "ingredient" && <IngredientDetails ingredient={modalData} />}
          {modal === "order" && <OrderDetails name={orderName} number={orderNumber} />} */}
      {/* {
            modal === "order"
              ? <OrderDetails name={orderName} number={orderNumber} />
              : <IngredientDetails ingredient={modalData} />
          }
        </Modal>

      } */}
    </div>
  );
}

export default App;
