import React, { useEffect } from "react";
import styles from "./modal.module.css";
import ReactDOM from "react-dom";
import PropTypes from "prop-types";
import ModalOverlay from "./modal-overlay/modal-overlay";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { closeModalAction } from "../../services/actions/modal-action"
import { useDispatch, useSelector } from "react-redux";

function Modal({ children, title, isVisible }) {
  const dispatch = useDispatch();
  const closeModal = React.useCallback(
    () => {
      dispatch(closeModalAction());
    },
    []
  );
  React.useEffect(() => {
    function escapePress(event) {
      if (event.key === "Escape") {
        closeModal();
      };
    };
    if (isVisible) {
      document.addEventListener("keydown", escapePress);
    };
    return () => {
      document.removeEventListener("keydown", escapePress);
    };
  },
    [isVisible]
  );

  const modalRoot = document.querySelector("#react-modals");
  return ReactDOM.createPortal((
    <>
      <div className={styles.container}>
        <div className={styles.content} >
          {/* {title && (
            <header className={`${styles.header} mt-10 ml-10 mr-10`}>
              <h2 className="text text_type_main-large">{title}</h2>
            </header>
          )}
          <button className={styles.closeIcon} onClick={closeModal}>
            <CloseIcon type="primary" />
          </button> */}
          {children}
        </div>
      </div>
      <ModalOverlay isVisible={isVisible} onClose={closeModal} />
    </>
  ),
    modalRoot
  );
};

Modal.propTypes = {
  title: PropTypes.string,
  onClose: PropTypes.func,
};

export default Modal;
