import { URL } from "./constants.js"

export const getResponse = () => {
  return fetch(`${URL}/ingredients`)
    .then((res) => res.ok
      ? res.json()
      : Promise.reject(`error: ${res.status} ${res.statusText}`))
    .then((data) => data.data);
}
