import { OPEN_MODAL_ORDER, OPEN_MODAL_INGREDIENT, CLOSE_MODAL } from "../actions/modal-action"

const initialState = {
  modal: null,
  isVisibleModal: false
};

export const modalReducer = (state = initialState, action) => {
  switch (action.type) {
    case OPEN_MODAL_ORDER: {
      return {
        ...state,
        modal: "order",
        isVisibleModal: true
      };
    }
    case OPEN_MODAL_INGREDIENT: {
      return {
        ...state,
        modal: "ingredient",
        isVisibleModal: true
      };
    }
    case CLOSE_MODAL: {
      return {
        modal: null,
        isVisibleModal: false
      };
    }
    default: {
      return state;
    }
  }
};
