export const OPEN_MODAL_ORDER = "MODAL_ORDER_OPEN"
export const OPEN_MODAL_INGREDIENT = "OPEN_MODAL_INGREDIENT"
export const CLOSE_MODAL = "CLOSE_MODAL"

export const openModal = (item) => {
  return {
    type: SET_CURRENT_INGREDIENT,
    payload: item
  };
}
