import React, { useMemo, useState } from "react";
import { useDrag, useDrop } from "react-dnd";
import styles from './burger-constructor-element.module.css'
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components"
import { useDispatch, useSelector } from "react-redux";
import { DragIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { useCallback, useRef } from "react";
import {
  ingredientsDataSelector,
  bunSelector,
  ingredientsIsLoadingSelector,
  burgerIngredientsSelector
} from "../../services/selectors/ingredients-selector";

import {
  DELETE_BURGER_INGREDIENT,
  changeOrderIngredients,
} from "../../services/actions/burger-constructor-action";



function BurgerConstructorElement({ noBun }) {
  const dispatch = useDispatch();
  const ref = useRef(null);

  const ingredients = useSelector(burgerIngredientsSelector);

  const changeIngredients = (dragIndex, hoverIndex, ingredients) => {
    dispatch(changeOrderIngredients(dragIndex, hoverIndex, ingredients));
  };

  // const handleRemoveElement = useCallback(() => {
  //   dispatch({
  //     type: DELETE_INGREDIENT,
  //     payload: index,
  //   });
  // }, [dispatch, index]);

  const [{ handlerId }, drop] = useDrop({
    accept: "burger-ingredient",
    collect(monitor) {
      return {
        handlerId: monitor.getHandlerId(),
      };
    },
    hover(item, monitor) {
      if (!ref.current) {
        return;
      }

      const dragIndex = item.index;
      const hoverIndex = index;

      if (dragIndex === hoverIndex) {
        return;
      }

      const hoverRect = ref.current?.getBoundingClientRect();
      const hoverCenterY = (hoverRect.bottom - hoverRect.top) / 2;
      const clientOffset = monitor.getClientOffset();
      const hoverClientY = clientOffset.y - hoverRect.top;

      if (
        (dragIndex < hoverIndex && hoverClientY < hoverCenterY) ||
        (dragIndex > hoverIndex && hoverClientY > hoverCenterY)
      ) {
        return;
      }

      changeIngredients(dragIndex, hoverIndex, ingredients);
      item.index = index;
    },
  });

  const [_, drag] = useDrag({
    type: "burger-ingredient",
    item: { index },
  });

  drag(drop(ref));
  return (
    <div className={`${styles.scroll} custom-scroll pr-2`}>
      {
        noBun.map((item, index) => {
          return (
            <div className={styles.elements} /*key={item.id}*/
              ref={ref}
              data-handler-id={handlerId}
            >
              <DragIcon type="primary" />
              <ConstructorElement
                text={item.name}
                price={item.price}
                thumbnail={item.image_mobile}



              // price={item.price}
              // thumbnail={item.image}
              // handleClose={() => handleRemoveElement()}
              />
            </div>
          )

        })
      }
    </div>




  )
}
export default BurgerConstructorElements;
